import AppContext from "../context/appContext";

function FilterModal() {
    const [item, setIt] = useState('');
    const [size, setSize] = useState('');
    const [color, setColor] = useState('');
    const [price, setPrice] = useState('');
    const [items, setItems] = useState([]);
  
    const handleSearch = async () => {
      const res = await fetch(`http://localhost:8080/products?type=${type}&size=${size}&color=${color}&price=${price}`);
      const filteredItems = await res.json();
      setItems(filteredItems);
    };
  
    return (
      <div>
        <input type="text" placeholder="Type" value={type} onChange={e => setType(e.target.value)} />
        <input type="text" placeholder="Size" value={size} onChange={e => setSize(e.target.value)} />
        <input type="text" placeholder="Color" value={color} onChange={e => setColor(e.target.value)} />
        <input type="text" placeholder="Price" value={price} onChange={e => setPrice(e.target.value)} />
        <button onClick={handleSearch}>Search</button>
        <ul>
          {items.map(item => (
            <li key={item.id}>{item.name}</li>
          ))}
        </ul>
      </div>
    );
  };

