import {
  FavoriteBorderOutlined,
  SearchOutlined,
  ShoppingCartOutlined,
} from "@mui/icons-material";
import React, { useContext, useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import AppContext from "../context/appContext";
import styled from "styled-components";

const Info = styled.div`
  opacity: 0;
  width: 100%;
  height: 100%;
  position: absolute;
  top: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.2);
  z-index: 3;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.5s ease;
  cursor: pointer;
`;

const Container = styled.div`
  flex: 1;
  margin: 5px;
  min-width: 280px;
  height: 350px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #f5fbfd;
  position: relative;
  &:hover ${Info} {
    opacity: 1;
  }
`;

const Circle = styled.div`
  width: 200px;
  height: 200px;
  border-radius: 50%;
  background-color: white;
  position: absolute;
`;

const Image = styled.img`
  height: 75%;
  z-index: 2;
`;

const Icon = styled.div`
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background-color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 10px;
  transition: all 0.5s ease;
  &:hover {
    background-color: #e9f5f5;
    transform: scale(1.1);
  }
`;

const GetAllProducts = ({ product }) => {
  const { baseUrl, user, token } = useContext(AppContext);
  const navigate = useNavigate();
  function navigateToProduct(product) {
    navigate(`/products/${product._id}`);
  }


  const addLikedProduct = async (product, email) => {
    try {
      
        const response = await axios.post('http://localhost:8080/users/add', { email, data: product });
        return response.data;
    } catch (error) {
        throw new Error(`Error adding product to wishlist: ${error}`);
    }
};

  // async function getWishlist() {
  //   try {
  //     const response = await axios.get(`${baseUrl}/users/${user.id}`);
  //     const data = response.data;
  //     console.log(data);
  //   } catch (error) {
  //     console.error(error);
  //   }
  // }


  // const addToWishlist = async (product) => {
  //   try {
  //     await axios.post(`${baseUrl}/users/add`, { product });
  //   } catch (error) {
  //     console.error(error);
  //   }
  // };

  // const [wishlist, setWishlist] = useState([]);

  // useEffect(() => {
  //   const fetchData = async () => {
  //     const items = await getWishlist();
  //     setWishlist(items);
  //   };
  //   fetchData();
  // }, []);

  // const handleAddToWishlist = (product) => {
  //   addToWishlist(product);
  //   setWishlist([...wishlist, product]);
  // };

  return (
    <Container>
      <Circle />
      <Image src={product.images} />
      <Info>
        <Icon>
          <ShoppingCartOutlined />
        </Icon>
        <Icon
          onClick={() => {
            navigateToProduct(product);
          }}>
          <SearchOutlined />
        </Icon>
        <Icon>
          <FavoriteBorderOutlined onClick={addLikedProduct}
          />
        </Icon>
      </Info>
    </Container>
  );
};

export default GetAllProducts;
