import React, { useState, useEffect } from "react";
import Home from "./page/Home";
import Login from "./page/Login";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import "./App.css";
import Signup from "./page/Signup";
import Welcome from "./page/Welcome";
import AllProducts from "./page/AllProducts";
import Cart from "./page/Cart";
import AppContext from "./context/appContext";
import axios from "axios";
import WishList from "./page/WishList";
import Product from "./page/Product";


function App() {
  const baseUrl = "http://localhost:8080";
  const [token, setToken] = useState("");
  const [productsList, setProductsList] = useState([])
  const [user, setUser]=useState("")
  const [logged, setLogged]= useState(false)

  useEffect(() => {
    const token = JSON.parse(localStorage.getItem("token"));
    if (token) {
      setToken(token);
      setLogged(true)
    }
  }, []);

  async function getUser() {
    const res = await axios.get(
      `http://localhost:8080/users/${user._id}`
    );
    setUser(res.data);
    console.log(user)
  }


  const fetchProducts = async () => {
    try {
      const response = await axios.get(`${baseUrl}/products`);
      const data = response.data;
      console.log(data)
      setProductsList(data)
      console.log(productsList)
  } catch (error) {
      console.log(error);
  }
};

useEffect(() => {
    fetchProducts();
}, []);

  return (
    <>
      <AppContext.Provider value={{ token, setToken, logged, setLogged, productsList, setProductsList, baseUrl, user, getUser }}>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/login" element={<Login />} />
            <Route path="/register" element={<Signup />} />
            <Route path="/e-shop" element={<Welcome />} />
            <Route path="/products" element={<AllProducts />} />
            <Route path="/products/:id" element={<Product />} />
            <Route path="/users/wishlist" element={<WishList />} />
            <Route path="/users/cart" element={<Cart />} />

          </Routes>
        </BrowserRouter>
      </AppContext.Provider>
    </>
  );
}

export default App;
