import Filter from "./Filter";
import React, { useState, useEffect } from "react";
import FilterAltOutlinedIcon from "@mui/icons-material/FilterAltOutlined";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";

  import AppContext from "../context/appContext";
import Modal from "react-bootstrap/Modal";
import axios from "axios";
function FilterModal() {
 const {fetchProducts} = useContext(AppContext);
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [searchTerm, setSearchTerm] = useState({
    item: "",
    gender: "",
    color: "",
    price: "",
    size: "",
  });
  const [filteredProducts, setFilteredProducts] = useState([]);

  const handleInputChange = (event) => {
    setSearchTerm({
      ...searchTerm,
      [event.target.name]: event.target.value,
    });
  };

  useEffect(() => {
    const fetchFilteredProducts = async () => {
      try {
        const response = await axios.get("http://localhost:8080/products", {
          params: searchTerm,
        });
        setFilteredProducts(response.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchFilteredProducts();
  }, [searchTerm]);
  return (
    <>
      <FilterAltOutlinedIcon onClick={handleShow}>
        Launch demo modal
      </FilterAltOutlinedIcon>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Item</Form.Label>
              <Form.Control
                type="text"
                name="item"
                value={searchTerm.item}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Color</Form.Label>
              <Form.Control
                type="text"
                name="color"
                value={searchTerm.color}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Catergory</Form.Label>
              <Form.Control
                type="text"
                name="gender"
                value={searchTerm.gender}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Size</Form.Label>
              <Form.Control
                type="text"
                name="gender"
                value={searchTerm.size}
                onChange={handleInputChange}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label>Price</Form.Label>
              <Form.Control
                type="text"
                name="price"
                value={searchTerm.price}
                onChange={handleInputChange}
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">
            Search
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default FilterModal;
