import { Badge } from "@mui/material";
import { Search, ShoppingCartOutlined } from "@mui/icons-material"
import React, {useContext, useState} from "react";
import styled from "styled-components";
import { mobile } from "../responsive";
import { useNavigate } from "react-router-dom";
import appContext from "../context/appContext";

const Container = styled.div`
  height: 60px;
  ${mobile({ height: "50px" })}
`;

const Wrapper = styled.div`
  padding: 10px 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  ${mobile({ padding: "10px 0px" })}
`;

const Left = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
`;

const Language = styled.span`
  font-size: 14px;
  cursor: pointer;
  ${mobile({ display: "none" })}
`;

const SearchContainer = styled.div`
  border: 0.5px solid lightgray;
  display: flex;
  align-items: center;
  margin-left: 25px;
  padding: 5px;
`;

const Input = styled.input`
  border: none;
  ${mobile({ width: "50px" })}
`;

const Center = styled.div`
  flex: 1;
  text-align: center;
`;

const Logo = styled.h1`
  font-weight: bold;
  ${mobile({ fontSize: "24px" })}
`;
const Right = styled.div`
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  ${mobile({ flex: 2, justifyContent: "center" })}
`;

const MenuItem = styled.div`
  font-size: 14px;
  cursor: pointer;
  margin-left: 25px;
  ${mobile({ fontSize: "12px", marginLeft: "10px" })}
`;
const NoMenuItem = styled.div`
  display:none
  ${mobile({ fontSize: "12px", marginLeft: "10px" })}
`;

const Navbar = () => {
  const { logged, setLogged} = useContext(appContext);
  let navigate = useNavigate()
  const [item, setItem] = useState("");
  const [color, setColor] = useState("");
  const [gender, setGender] = useState("");
  const [price, setPrice] = useState("");
  const [searchProduct, setSearchProduct] = useState([]);

  function handleSearch(e) {
    e.preventDefault();

    axios
      .get(
        `http://localhost:8080/product/search?item=${type}&name=${name}&status=${status}&weight=${weight}&height=${height}`
      )
      .then((response) => {
        setSearchPet(response.data);
      })
      .catch((error) => {
        console.log("Error getting data: " + error);
      });
  }

  function Logging(){
    setLogged(true);
    navigate("/login")
    setLogged(false);
  }

  function Logout(){
    setLogged(false);
    navigate("/")
    localStorage.clear();
  }

  function Register(){
    navigate("/register")
  }

  function Home(){
    navigate("/")
  }

  function Cart(){
    navigate("/users/cart")
  }

  function Wishlist(){
    navigate("/users/:id/wishlist")
  }



  return ( 
    <>
    <Container>
      <Wrapper>
        <Left>
          <Language>EN</Language>
          <SearchContainer>
            <Input placeholder="Search" />
            <Search style={{ color: "gray", fontSize: 16 }} />
          </SearchContainer>
        </Left>
        <Center>
          <Logo onClick={Home}>E-SHOP</Logo>
        </Center>
        <Right>
        {logged ? (<MenuItem onClick={Wishlist}>WishList</MenuItem>):(<MenuItem onClick={Register}>Register</MenuItem>) }
          {logged ?(<MenuItem onClick={Logout}>Log Out</MenuItem>) : (<MenuItem onClick={Logging}>Sign In</MenuItem>)} 
          <MenuItem>
            <Badge badgeContent={4} color="primary">
              <ShoppingCartOutlined onClick={Cart} />
            </Badge>
          </MenuItem>
        </Right>
      </Wrapper>
    </Container>
    </>
  );
};

export default Navbar;
